import React from 'react'
import './styles.css'
import PropTypes from 'prop-types'

const Square = (props) => {
	let style = {}
	let num = props.num % 3
	if (num === 0)
		style.borderLeft = "1vw solid black"
	else if (num === 2)
		style.borderRight = "1vw solid black"
	console.log(props.num)
	return (
		<button
			className="square"
			style={style}
			onClick={() => props.onClick()}
		>
			{props.value}
		</button>
	)
}

Square.propTypes = {
	onClick: PropTypes.func,
	value: PropTypes.string
}
export default Square
