import React from 'react'
import './styles.css'
import PropTypes from 'prop-types'

const Square = (props) => {
	let style = {}

	let num = props.num % 3
	if (num === 0)
		style.borderLeft = "0.2vw solid black"
	else if (num === 2)
		style.borderRight = "0.2vw solid black"

	num = props.num % 9
	if (num >= 0 && num <= 2)
		style.borderTop = "0.2vw solid black"
	else if (num >= 6 && num <= 8)
		style.borderBottom = "0.2vw solid black"

	if (props.value === 'X')
		style.color = "red"
	else if (props.value === 'O')
		style.color = "lightblue"

	if (props.belongsTo === 'X')
		style.background = "maroon"
	else if (props.belongsTo === 'O')
		style.background = "blue"
	else {
		if (props.nextSquare === -1)
			style.background = "green"
		else if (props.nextSquare === num)
			style.background = "green"
		else
			style.background = "white"
	}



	return (
		<button
			className="square"
			style={style}
			onClick={() => props.onClick()}
		>
			{props.value}
		</button>
	)
}

Square.propTypes = {
	onClick: PropTypes.func,
	value: PropTypes.string
}
export default Square
