let calculateWinner = (squares) => {
    const lines = [
        [0, 1, 2],
        [3, 4, 5],
        [6, 7, 8],
        [0, 3, 6],
        [1, 4, 7],
        [2, 5, 8],
        [0, 4, 8],
        [2, 4, 6]
    ]
    for (let i = 0; i < lines.length; i++) {
        const [a, b, c] = lines[i]
        if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c])
            return squares[a]
    }
    return null
}

let getStyle = (num) => {
    let style = {}

	if (num === 0)
		style.borderLeft = "0.2vw solid black"
	else if (num === 2)
		style.borderRight = "0.2vw solid black"

	num = props.num % 9
	if (num >= 0 && num <= 2)
		style.borderTop = "0.2vw solid black"
	else if (num >= 6 && num <= 8)
		style.borderBottom = "0.2vw solid black"

	if (props.value === 'X')
		style.color = "red"
	else if (props.value === 'O')
		style.color = "lightblue"

	if (props.belongsTo === 'X')
		style.background = "maroon"
	else if (props.belongsTo === 'O')
		style.background = "blue"
	else {
		if (props.green)
			style.background = "lightgreen"
		else
			style.background = "white"
	}

	return style
}

export {calculateWinner, getStyle}
